mapper.update:

update  ph_board
set     hit = hit + 1
where   seq = #{seq}

mapper.select:
select * from ph_board where 1=1
and (title like CONCAT('%',#{keyword},'%') or content like CONCAT('%',#{keyword},'%') or writer like CONCAT('%',#{keyword},'%'))
order by seq desc limit #{displayPost}, #{postNum}
